mapper.update:
UPDATE BOARD
SET BOARD_VIEWS = BOARD_VIEWS + 1
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT MEMBER_NICKNAME
FROM MEMBER
WHERE MEMBER_NO = #{memberNo}

mapper.update:
UPDATE BOARD
SET STATUS = 'N'
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT COUNT(*)
FROM LIKE_BOARD
WHERE 
	BOARD_NO = #{boardNo}
	AND MEMBER_NO = #{memberNo}
	AND STATUS = 'Y'

mapper.insert:
INSERT INTO LIKE_BOARD(
	BOARD_NO,
	MEMBER_NO
) VALUES (
	#{boardNo},
	#{memberNo}
)

mapper.select:
SELECT COUNT(*)
FROM LIKE_BOARD
WHERE 
	MEMBER_NO = #{memberNo}
	AND BOARD_NO = #{boardNo}
	AND STATUS = 'Y'

SELECT COUNT(*)
FROM SCRAP_BOARD
WHERE 
	STATUS = 'Y' 
	AND MEMBER_NO = #{memberNo}
	AND BOARD_NO = #{boardNo}

mapper.insert:
INSERT INTO SCRAP_BOARD(
	MEMBER_NO,
	BOARD_NO
) VALUES (
	#{memberNo},
	#{boardNo}
)

mapper.select:
SELECT COUNT(*)
FROM SCRAP_BOARD
WHERE 
	MEMBER_NO = #{memberNo}
	AND BOARD_NO = #{boardNo}
	AND STATUS = 'Y'

SELECT COUNT(*)
FROM LIKE_BOARD
WHERE
	STATUS = 'Y' 
	AND MEMBER_NO = #{memberNo}
	AND BOARD_NO = #{boardNo}
