mapper.select:
SELECT COUNT(FOLLOWING_NO)
FROM FOLLOW
WHERE FOLLOWING_NO = #{memberNo}

SELECT COUNT(FOLLOWER_NO)
FROM FOLLOW
WHERE FOLLOWING_NO = #{memberNo}

mapper.update:
UPDATE RECIPE
SET STATUS = 'N'
WHERE RCP_NO = #{recipeNo}

mapper.select:
SELECT COUNT(*) FROM ( SELECT B.BOARD_NO, B.BOARD_TITLE, B.BOARD_CREATE_DATE, B.BOARD_VIEWS, B.BOARD_THUMBNAIL, B.STATUS, B.MEMBER_NO, COALESCE(l.like_count, 0) AS like_count FROM BOARD B LEFT JOIN ( SELECT board_no, COUNT(*) AS like_count FROM public.like_board GROUP BY board_no ) l ON b.board_no = l.board_no WHERE B.STATUS = 'Y' AND B.MEMBER_NO = #{memberNo}
ORDER BY B.BOARD_CREATE_DATE DESC
ORDER BY B.BOARD_VIEWS DESC
ORDER BY LIKE_COUNT DESC
)

mapper.update:
UPDATE BOARD
SET STATUS = 'N'
WHERE BOARD_NO = #{boardNo}

UPDATE SCRAP_RECIPE
SET STATUS = 'N'
WHERE MEMBER_NO = #{memberNo} AND RCP_NO = #{recipeNo}

UPDATE PRODUCT
SET SCRAP_COUNT = SCRAP_COUNT - 1
WHERE PRODUCT_NO = #{productNo}

UPDATE LIKE_RECIPE
SET STATUS = 'N'
WHERE MEMBER_NO = #{memberNo} AND RCP_NO = #{recipeNo}

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE 
	MEMBER_NO != #{memberNo}
	AND MEMBER_NICKNAME = #{nickname}

SELECT COUNT(*)
FROM MEMBER
WHERE 
	MEMBER_NO != #{memberNo}
	AND MEMBER_PHONE LIKE #{memberPhone}

mapper.update:
UPDATE MEMBER
SET MEMBER_STATUS = 'N'
WHERE MEMBER_NO = #{memberNo}

mapper.select:
select count(ref_no)
from refrigerator
where member_no = #{memberNo}

select r.RCP_NO, r.RCP_TITLE, r.RCP_MAIN_PHOTO from recipe r join division d using (rcp_no) join ingredients_info i using (div_no) where r.status = 'Y' and i.ingre_name in
#{ingredi}
LIMIT 10

select RCP_NO, RCP_TITLE, RCP_MAIN_PHOTO from recipe where status = 'Y' and RCP_NO in
#{recipeNo}
LIMIT 10
