mapper.select:
INSERT INTO DIVISION (
	div_name,
	rcp_no
) VALUES(
	#{divName},
	#{recipeNo}
) 	
RETURNING *

INSERT INTO COOK_ORDER (
	cook_ord_content,
	cook_ord_photo,
	rcp_no

) VALUES(
	#{cookOrdContent},
	#{cookOrdPhoto},
	#{recipeNo}
) 	
RETURNING *

mapper.insert:
INSERT INTO COOK_TIP(
	cook_tip_content,
	cook_ord_no
) VALUES(
	#{cookTipContent},
	#{cookOrdNo}
)

mapper.delete:
DELETE FROM DIVISION
WHERE div_no = #{divNo}
AND rcp_no = #{recipeNo};

DELETE FROM COOK_ORDER
WHERE rcp_no=#{recipeNo}
AND cook_ord_no =#{cookOrdNo};
